import React, { useEffect } from 'react';
import Header from "../../dumb-components/Header"

import Carousel from 'react-spring-3d-carousel';
import { config } from "react-spring";
import YouTube from 'react-youtube';



export default function Home() {

    const opts = {
        height: '500px',
        width: '100%',
        playerVars: 'https://developers.google.com/youtube/player_parameters'
        ,
    };
    const slides = [
        {
            key: 1,
            content: <YouTube videoId="2g811Eo7K8U" opts={opts} />
        },
        {
            key: 2,
            content: <YouTube videoId="2g811Eo7K8U" opts={opts} />
        },
        {
            key: 3,
            content: <YouTube videoId="2g811Eo7K8U" opts={opts} />
        }

    ];

    return (
        <>
            <Header />
            <div className="component-video-gallery container-holder">
                <h1 className="opacity-50 font-500">Concept Videos</h1>
                <h6 className="opacity-50 font-400 text-12">Select a number</h6>
                <div style={{ width: "100%", height: "500px", margin: "0 auto" }}>
                <Carousel showNavigation={true}  offsetRadius={5}animationConfig={config.gentle } slides={slides} />
                </div>
            </div>

        </>
    )
}
